byDocId.map:
function(doc) { 

    if (doc.head.contentType == "personProfile")
    {
	emit([doc._id, "person: " + doc.name.last + ", " + doc.name.first], doc);
    }
    else if(doc.head.contentType == "outline" || doc.head.contentType == "chiasm" || doc.head.contentType == "panel") 
    { 
	if (doc.head.author && doc.head.author.guid)
		emit([doc._id, doc.head.contentType + ": " + doc.head.author.authorShortname], doc);
	else
		emit([doc._id, doc.head.contentType + ": unspecified author"], doc); 
    }
    else if (doc.head.contentType == "sourceProfile")
    {
	emit([doc._id, "source: " + doc.details.substr(0, 64)], doc);
    }

}

